        val elementAtCursor = psiFile.findElementAt(offset)

        if (elementAtCursor?.text?.replace(" ", "")?.endsWith("\n\n") == true) return ""

        element = findElementToLogForSelection(elementAtCursor!!)
        valueToLog = element?.text?.replace(" ", "") ?: "<CR>"
    }

